// NOTE: FREERTOS timing needs to be set to 1000hz!
// This allows us to use vTaskDelay and still keep our watchdog timer
// Dont need to divide vTaskDelay by anything, as 1 tick = 1 ms

#define LEDC_TIMER LEDC_TIMER_0
#define LEDC_MODE LEDC_HIGH_SPEED_MODE
#define LEDC_OUTPUT_IO (2) // Define the output GPIO
#define LEDC_CHANNEL LEDC_CHANNEL_0
#define LEDC_DUTY_RES 1       // Set duty resolution to 1 bit
#define LEDC_DUTY (1)         // Set duty to 50%. ((2 ** 13) - 1) * 50% = 4095
#define LEDC_FREQUENCY (5000) // Frequency in Hertz. Set frequency at 5 kHz

#define BANNER_HEIGHT 16

#define SSTV_1100HZ 1100
#define SSTV_1200HZ 1200
#define SSTV_1300HZ 1300
#define SSTV_1500HZ 1500
#define SSTV_1900HZ 1900

// Milli/Microseconds needed for different parts of Martin 2 SSTV message
#define SSTV_MARTIN_2_PULSE_MS 4
#define SSTV_MARTIN_2_PULSE_US 862 // Actually 4862 but add 4ms as vTaskDelay for watchdog
#define SSTV_MARTIN_2_PORCH_US 572
#define SSTV_MARTIN_2_SCAN_US 229
#define SSTV_MARTIN_2_SEPARATOR_US 572

// Pick color for SSTV scanlines
#define RED 0
#define GREEN 1
#define BLUE 2

// Martin 2 VIS is 40 (0b101000) (LSB = 0b000101), 0 in front, and even parity (0) in back.
#define NUM_VIS_BITS 8
static const uint8_t SSTV_MARTIN_2_VIS_BITS[8] = {0, 0, 0, 0, 1, 0, 1, 0};

